package gov.va.bip.vetservices.claims.messages;

import java.util.Locale;

import org.springframework.context.support.ReloadableResourceBundleMessageSource;

import gov.va.bip.framework.messages.MessageKey;

/**
 * The source for messages generated by the micro-service code.
 *
 * @author rajuthota
 */
public enum ClaimsMessageKeys implements MessageKey {

	
	BIP_CLAIM_DETAIL_INFO_REQUEST_NOTNULL("bip.vetservices.claims.claimdetail.request.NotNull",
			"ClaimDeatilRequest Payload cannot be null"),
	
	BIP_CLAIMS_INFO_REQUEST_NOTNULL("bip.vetservices.claims.info.request.NotNull",
			"ClaimsInfoRequest Payload cannot be null"),
	
	/** PID cannot be null validation; no args */
	BIP_CLAIMS_INFO_REQUEST_PID_NOTNULL("bip.vetservices.claims.request.pid.NotNull",
			"ClaimInfoRequest.participantID cannot be null"),
	/** Claim ID is not valid */
	BIP_CLAIMS_INFO_REQUEST_CLAIMID_NOTVALID("bip.vetservices.claims.request.claimId.NotValid",
			"ClaimInfoRequest.claimId is not valid"),
	/** Claim ID cannot be null validation; no args */
	BIP_CLAIMS_INFO_REQUEST_CLAIMID_NOTNULL("bip.vetservices.claims.request.claimId.NotNull",
			"ClaimInfoRequest.claimId cannot be null");


	/** The filename "name" part of the properties file to get from the classpath */
	private static final String propertiesFile = "messages";
	/** The message source containing properties for this enum */
	private static ReloadableResourceBundleMessageSource messageSource;
	/* Populate the message source from the properties file */
	static {
		messageSource = new ReloadableResourceBundleMessageSource();
		messageSource.setBasename("classpath:" + propertiesFile);
		messageSource.setDefaultEncoding("UTF-8");
	}

	/** The key - must be identical to the key in framework-messages.properties */
	private String key;
	/** A default message, in case the key is not found in framework-messages.properties */
	private String defaultMessage;

	/**
	 * Construct keys with their property file counterpart key and a default message.
	 *
	 * @param key - the key as declared in the properties file
	 * @param defaultMessage - in case the key cannot be found
	 */
	private ClaimsMessageKeys(String key, String defaultMessage) {
		this.key = key;
		this.defaultMessage = defaultMessage;
	}

	/*
	 * (non-Javadoc)
	 *
	 * @see gov.va.bip.framework.messages.MessageKey#getKey()
	 */
	@Override
	public String getKey() {
		return this.key;
	}

	/*
	 * (non-Javadoc)
	 *
	 * @see gov.va.bip.framework.messages.MessageKey#getMessage(java.lang.Object[])
	 */
	@Override
	public String getMessage(String... params) {
		return messageSource.getMessage(this.getKey(), params, this.defaultMessage, Locale.getDefault());
	}
}
